    private void subActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_subActionPerformed
        // TODO add your handling code here:
        String user=username.getText();
        String pas=pass.getText();
        
        if(user.equals("abc")&&pas.equals("123"))
        {Menu m=new Menu();
         m.setVisible(true);
        dispose();}
        else
        {JOptionPane.showMessageDialog(rootPane,"Either username or password is incorrect......");}
        
    }//GEN-LAST:event_subActionPerformed

    private void cbActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbActionPerformed
        // TODO add your handling code here:
        String c=code.getText();
        if(c.equals("000"))
            JOptionPane.showMessageDialog(rootPane,"Your Username=abc\nYour Password=123");
        else
            JOptionPane.showMessageDialog(rootPane,"You forgotten secret code too......");
    }//GEN-LAST:event_cbActionPerformed

